mapper.select:

select deviceno,devicename,devicetype,devicevender,devicemodel,
ip,port,devicesn,devicecode,
maccode,username,userpass,online,netstate,enabled,iostatus,doorname,
date_format(createdate,'%Y-%m-%d %H:%i:%s') createdate,
deviceuse,channelno,areaid from DevicePlat


		and deviceno=#{deviceno}


		and devicename=#{devicename}


		and devicename like concat('%',#{name},'%')


		and devicesn=#{devicesn}


		and iostatus=#{iostatus}


		and online=#{online}


		and enabled=#{enabled}


		and devicetype=#{devicetype}

select count(1) from DevicePlat


		and deviceno=#{deviceno}


		and devicename=#{devicename}


		and devicename like concat('%',#{name},'%')


		and devicesn=#{devicesn}


		and iostatus=#{iostatus}


		and online=#{online}


		and enabled=#{enabled}


		and devicetype=#{devicetype}
