mapper.select:
     select id,holderno,holdername,unitno,unitname,state,type,reason,level,
     date_format(startdate,'%Y-%m-%d %H:%i:%s') startdate,
     date_format(enddate,'%Y-%m-%d %H:%i:%s') enddate,
     applystatus,approver1,approver2,approver3,approver4,failreason,flowtracing,
     date_format(createdate,'%Y-%m-%d %H:%i:%s') createdate,
     groupid,(select name from AccessGroup where id=HolderLeave.groupid) groupname  from holderleave 
     where (ApplyStatus='10' 

	       and Approver1=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

          )or  (ApplyStatus='20' 

		       and Approver2=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        ) or  (ApplyStatus='30' 

		       and Approver3=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        ) or  (ApplyStatus='40' 

		       and Approver4=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        )

     select count(1) from holderleave 
     where (ApplyStatus='10' 

	       and Approver1=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

         )or  (ApplyStatus='20' 

		       and Approver2=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        ) or  (ApplyStatus='30' 

		       and Approver3=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        ) or  (ApplyStatus='40' 

		       and Approver4=#{loginid}


	        and state=#{state}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and HolderName like concat('%',#{name},'%')

        )


     select id,holderno,holdername,unitno,unitname,state,type,reason,level,
     date_format(startdate,'%Y-%m-%d %H:%i:%s') startdate,
     date_format(enddate,'%Y-%m-%d %H:%i:%s') enddate,
     applystatus,approver1,approver2,approver3,approver4,failreason,remark,flowtracing,
     date_format(createdate,'%Y-%m-%d %H:%i:%s') createdate,
     groupid,(select name from AccessGroup where id=HolderLeave.groupid) groupname  from HolderLeave 


	        and ID=#{id}


	        and holderno=#{holderno}


	        and HolderName like concat('%',#{name},'%')


	        and state=#{state}


	        and applystatus=#{applystatus}


	        and  date_format(createdate,'%Y-%m-%d')=#{date}


	        and  date_format(startdate,'%Y-%m-%d %H:%i:%s') <=#{date1}  and  date_format

(enddate,'%Y-%m-%d %H:%i:%s') >=#{date1}


	        and  date_format(startdate,'%Y-%m-%d %H:%i:%s') <=#{date2} and date_format

(enddate,'%Y-%m-%d %H:%i:%s') >=#{date2}


	        and createperson=#{createperson}

	select count(1) from HolderLeave 


        and ID=#{id}


        and holderno=#{holderno}


        and HolderName like concat('%',#{name},'%')


        and state=#{state}


        and applystatus=#{applystatus}


        and  date_format(createdate,'%Y-%m-%d')=#{date}


        and  date_format(startdate,'%Y-%m-%d %H:%i:%s') <=#{date1}  
        and  date_format(enddate,'%Y-%m-%d %H:%i:%s') >=#{date1}


        and  date_format(startdate,'%Y-%m-%d %H:%i:%s') <=#{date2} 
        and date_format(enddate,'%Y-%m-%d %H:%i:%s') >=#{date2}
